div.script:
// Replace this with your actual cart data
const cartData = [
{ name: "Product 1", rate: "$10.00" },
{ name: "Product 2", rate: "$20.00" },
// Add more cart items as needed
];

// Function to populate the cart items in the table
function populateCartItems() {
const cartItemsContainer = document.getElementById("cartItems");

cartData.forEach(item => {
const row = document.createElement("tr");
const productNameCell = document.createElement("td");
productNameCell.textContent = item.name;
const rateCell = document.createElement("td");
rateCell.textContent = item.rate;

row.appendChild(productNameCell);
row.appendChild(rateCell);
cartItemsContainer.appendChild(row);
});
}

// Call the function to populate cart items when the page loads
populateCartItems();

form.script:
// Get references to the First Name and Last Name input fields
const firstNameInput =
document.querySelector('[name="first_name"]');
const lastNameInput = document.querySelector('[name="last_name"]');

// Get a reference to the Name input field
const nameInput = document.querySelector('[name="name"]');

// Add an event listener to update the Name field when either First
Name or Last Name changes
firstNameInput.addEventListener('input', updateName);
lastNameInput.addEventListener('input', updateName);

// Function to update the Name field
function updateName() {
const firstName = firstNameInput.value;
const lastName = lastNameInput.value;
nameInput.value = `${firstName} ${lastName}`;
}
